import './App.css';

import React, { useState } from 'react';
import CardsList from './CardsList';
import Card from './Card';

function App() {



  const [cards, setCards] = React.useState([
    {
      movieTitle: 'inception',
      movieDescription: 'new movie 2013 ....',
      movieRate: '5',
      movieImage: 'inception.jpeg',
    },

  ])
  const newCard = {
    movieTitle: 'zayneb',
    movieDescription: '',
    movieRate: '',
    movieImage: ''
  }
  const add = (titleAdded, descriptionAdded, rateAdded, imageAdded) => {
    setCards([...cards, { ...newCard, movieTitle: titleAdded, movieDescription: descriptionAdded, movieRate: rateAdded, movieImage: imageAdded }])
  }
  const [records, setRecords] = useState(cards)
  const Filter = (event) => {
    setRecords(cards.filter(f => f.movieTitle.toLowerCase().includes(event.target.value)))
  }

  return (
    <div className="App">
      <h1>Movies List</h1>
      <input type='text' onChange={Filter} placeholder='Search' />

      {/* <CardsList  handleAddCards={addCard} /> */}
      <CardsList mesCards={cards} handleAdd={add} />

    </div>
  );
}

export default App;
