        <div style={{ margin: '4rem 2rem' }} >
            <h1 style={{ color: 'white' }}>{titre}</h1>

            <p style={{ color: 'white' }}>
                {location.state.desc}
            </p>
            <Container>
                <div className="ratio ratio-21x9">
                    <iframe src={`${location.state.annonce}`} title="YouTube video" allowFullScreen></iframe>
                </div>
                <div className='trailer-description'>
                </div>
            </Container >



        </div >
    )
}
