import './App.css';

import React from 'react';
import CardsList from './CardsList';

function App() {

  const [cards, setCards] = React.useState([
    {
      image: 'inception.jpeg',
      title: 'inception',
      description: 'new movie ....',
      rate: '5'
    },
    {
      image: 'pride.jpg',
      title: 'Pride and prejudice',
      description: 'new movie 2017. ....',
      rate: '6'
    },
  ])
  const addCard = (image, title, description, rate) => {
    const newCard = {
      image: image,
      title: title,
      description: description,
      rate: rate,
    }
    const newCards = [...cards, newCard]
    setCards(newCards)
  }

  return (
    <div className="App">
      <h1>Movies List</h1>

      <CardsList mesCards={cards} handleAddCards={addCard} />

    </div>
  );
}

export default App;
