import React, { useState } from 'react'
import Cards from './Card';

export default function MovieList() {

    const [list, setList] = useState([])

    function addMovie() {
        setList(prevList => {
            return [...prevList, <Cards />]
        })
    }
    const movieList = list.map(elm =>
        <div>{elm}</div>
    )

    return (

        <div> <button className='' onClick={addMovie}> Add Movie</button>
            {movieList}</div>
    )
}
