import React from 'react'
import './card.css'

export default function AAddCard({ handleAddCards }) {

    const [movieCard, setMovieCard] = React.useState({
        cardText: '',
        cardImage: '',
    })



    //////////////////////// Image ///////////////////
    const inputRef = React.useRef(null)
    const handleImageClick = () => {
        inputRef.current.click()
    }
    const handleImageChange = (event) => {
        const file = event.target.files[0];
        console.log(file);
        setMovieCard(prevCardData => {
            return {
                ...prevCardData,
                [event.target.name]: event.target.files[0]
            }
        })
    }

    ///////////////////////////////

    const handleChange = (event) => {
        setMovieCard(prevCardMovie => {
            return {
                ...prevCardMovie,
                [event.target.name]: event.target.value
            }
        })
    }

    const handleSaveClick = () => {
        handleAddCards(movieCard.cardText)
    }
    return (
        <div className='new-movie-card'>
            <div onClick={handleImageClick}>
                <label htmlFor='image-input'>
                    {
                        movieCard.cardImage ? <img className='upload-image' src={URL.createObjectURL(movieCard.cardImage)} alt='' /> : <img className='upload-image' src={require('./images/upload.jpeg')} alt='' />
                    }
                </label>
                <input type='file'
                    ref={inputRef}
                    name='cardImage'
                    onChange={handleImageChange}
                    style={{ display: 'none' }} />
            </div>
            <br />
            <input type='text'
                placeholder='type title'
                onChange={handleChange}
                value={movieCard.cardText}
                name='cardTitle'
                className='movie-title'
            />
            <br />

            <textarea
                // rows='10'
                // cols='10'
                placeholder='type here...'
                onChange={handleChange}
                value={movieCard.cardText}
                name='cardText'
                className='movie-description'
            >
            </textarea>
            <br />

            <input type='text'
                placeholder='Rate...'
                onChange={handleChange}
                value={movieCard.cardRate}
                name='cardRate'
                className='movie-rate'
            />
            <br />
            <button onClick={handleSaveClick}> Save</button>
        </div>
    )
}
