import React from 'react'
import './card.css'

export default function AAddCard(props) {

    const [newCard, setnewCard] = React.useState({
        newTitle: '',
        newDescription: '',
        newRate: '',

    })



    //////////////////////// Image ///////////////////
    // const inputRef = React.useRef(null)
    // const handleImageClick = () => {
    //     inputRef.current.click()
    // }
    // const handleImageChange = (event) => {
    //     const file = event.target.files[0];
    //     console.log(file);
    //     setAddCard(prevCardData => {
    //         return {
    //             ...prevCardData,
    //             [event.target.name]: event.target.files[0]
    //         }
    //     })
    // }

    ///////////////////////////////

    const handleChange = (event) => {
        setnewCard(prevCard => {
            return {
                ...prevCard,
                [event.target.name]: event.target.value
            }
        })
    }

    const handleSaveClick = () => {
        props.handleAddCard(newCard.newTitle, newCard.newDescription, newCard.newRate);
        // newCard.newImage
    }

    return (
        <div className='new-movie-card'>
            {/* 
            <div onClick={handleImageClick}>
                <label htmlFor='image-input'>
                    {
                        newCard.newImage ? 
                        <img className='upload-image' src={URL.createObjectURL(newCard.newImage)} alt='' /> : <img className='upload-image' src='./images/upload.jpeg' alt='' />
                    }
                </label>
                <input type='file'
                    ref={inputRef}
                    name='newImage'
                    onChange={handleImageChange}
                    style={{ display: 'none' }} />
            </div> */}




            <br />
            <input type='text'
                placeholder='Title...'
                onChange={handleChange}
                value={newCard.newTitle}
                name='newTitle'
                className='movie-title'
            />
            <br />

            <textarea
                // rows='10'
                // cols='10'
                placeholder='Description...'
                onChange={handleChange}
                value={newCard.newDescription}
                name='newDescription'
                className='movie-description'
            >
            </textarea>
            <br />

            <input type='text'
                placeholder='Rate...'
                onChange={handleChange}
                value={newCard.newRate}
                name='newRate'
                className='movie-rate'
            />
            <br />
            <button onClick={handleSaveClick}> Save</button>
        </div>
    )
}
