import React from 'react'
import './card.css'

export default function AAddCard(props) {

    const [addCard, setAddCard] = React.useState({
        Title: '',
        Description: '',
        Rate: '',
        Image: '',
    })



    //////////////////////// Image ///////////////////
    // const inputRef = React.useRef(null)
    // const handleImageClick = () => {
    //     inputRef.current.click()
    // }
    // const handleImageChange = (event) => {
    //     const file = event.target.files[0];
    //     console.log(file);
    //     setAddCard(prevCardData => {
    //         return {
    //             ...prevCardData,
    //             [event.target.name]: event.target.files[0]
    //         }
    //     })
    // }

    ///////////////////////////////

    const handleChange = (event) => {
        setAddCard(prevAddCard => {
            return {
                ...prevAddCard,
                [event.target.name]: event.target.value
            }
        })
    }

    const handleSaveClick = () => {
        props.handleAddCard(addCard.Title, addCard.Description, addCard.Rate);
    }

    return (
        <div className='new-movie-card'>
            {/* 
            <div onClick={handleImageClick}>
                <label htmlFor='image-input'>
                    {
                        addCard.Image ? 
                        <img className='upload-image' src={URL.createObjectURL(addCard.Image)} alt='' /> : <img className='upload-image' src='./images/upload.jpeg' alt='' />
                    }
                </label>
                <input type='file'
                    ref={inputRef}
                    name='Image'
                    onChange={handleImageChange}
                    style={{ display: 'none' }} />
            </div> */}




            <br />
            <input type='text'
                placeholder='Title...'
                onChange={handleChange}
                value={addCard.Title}
                name='Title'
                className='movie-title'
            />
            <br />

            <textarea
                // rows='10'
                // cols='10'
                placeholder='Description...'
                onChange={handleChange}
                value={addCard.Description}
                name='Description'
                className='movie-description'
            >
            </textarea>
            <br />

            <input type='text'
                placeholder='Rate...'
                onChange={handleChange}
                value={addCard.Rate}
                name='Rate'
                className='movie-rate'
            />
            <br />
            <button onClick={handleSaveClick}> Save</button>
        </div>
    )
}
