import './App.css';

import React from 'react';
import CardsList from './CardsList';
import Card from './Card';

function App() {

  const [cards, setCards] = React.useState([
    {
      movieTitle: 'inception',
      movieDescription: 'new movie 2013 ....',
      movieRate: '5',
      // image: 'inception.jpeg',
    },

  ])
  const newCard = {
    movieTitle: '',
    movieDescription: '',
    movieRate: ''
  }
  const addTitle = (titleAdded,) => {

    setCards([...cards, { ...newCard, movieTitle: titleAdded }])
  }
  const addDesc = (descriptionAdded) => {
    const newList = [...cards, { ...newCard, movieDescription: descriptionAdded }]
    setCards(newList)
  }

  return (
    <div className="App">
      <h1>Movies List</h1>

      {/* <CardsList  handleAddCards={addCard} /> */}
      <CardsList mesCards={cards} handleAddT={addTitle} handleAddD={addDesc} />


    </div>
  );
}

export default App;
