import React from 'react'
import Card from './Card'
import AAddCard from './AAddCard'
import './card.css'


export default function CardsList({ addTitle, addDescription, addRate }) {

    const [cards, setCards] = React.useState([
        {
            movieTitle: 'inception',
            movieDescription: 'new movie 2013 ....',
            // image: 'inception.jpeg',
            rate: '5'
        },

    ])
    const CardsList = cards.map(card => (
        <Card
            title={card.movieTitle}
            description={card.movieDescription}
            // image={card.cardImage}
            rate={card.cardRrate}
        />
    ))


    const addCard = (addTitle, addDescription, addRate) => {

        setCards(prevCards => {
            return [...prevCards,
            <Card
                title={addTitle}
                description={addDescription}
                rate={addRate}
            // image: addImage,
            />]

        })
    }



    return (

        <div>
            <div className='movie-list'>
                {CardsList}
            </div>
            <AAddCard handleSaveClick={addCard} />
        </div>
    )
}
