import './App.css';

import React from 'react';
import CardsList from './CardsList';

function App() {

  const [cards, setCards] = React.useState([
    {
      image: 'inception.jpeg',
      title: 'fdgdhd',
      description: 'new movie',
      rate: '5'

    },
    {

      text: 'fdgdhd',
      image: 'inception.jpeg'
    },
  ])
  const addCard = (text, image) => {
    console.log(text)
    const newCard = {

      text: text,
      image: image,
    }
    const newCards = [...cards, newCard]
    setCards(newCards)
  }

  return (
    <div className="App">
      <h1>Movies List</h1>

      <CardsList mesCards={cards} handleAddCards={addCard} />

    </div>
  );
}

export default App;
