import React, { useState } from 'react'

export default function MovieList() {

    const [list, setList] = useState(['item1', 'item2'])

    function addMovie() {
        setList(prevList => {
            return [...prevList, `item X`]
        })
    }
    const movieList = list.map(elm =>
        <div>{elm}</div>
    )

    return (

        <div> <button className='' onClick={addMovie}> Add Movie</button>
            {movieList}</div>
    )
}
