import './App.css';

import React from 'react';
import CardsList from './CardsList';
import Card from './Card';

function App() {

  const [cards, setCards] = React.useState([
    {
      movieTitle: 'inception',
      movieDescription: 'new movie 2013 ....',
      movieRate: '5',
      // image: 'inception.jpeg',
    },

  ])
  const newCard = {
    movieTitle: '',
    movieDescription: '',
    movieRate: ''
  }
  const add = (titleAdded, descriptionAdded, rateAdded) => {
    setCards([...cards, { ...newCard, movieTitle: titleAdded, movieDescription: descriptionAdded, movieRate: rateAdded }])
  }

  console.log(cards)

  return (
    <div className="App">
      <h1>Movies List</h1>

      {/* <CardsList  handleAddCards={addCard} /> */}
      <CardsList mesCards={cards} handleAdd={add} />

    </div>
  );
}

export default App;
