import './App.css';

import React from 'react';
import CardsList from './CardsList';
import Card from './Card';

function App() {

  const [cards, setCards] = React.useState([
    {
      movieTitle: 'inception',
      movieDescription: 'new movie 2013 ....',
      // image: 'inception.jpeg',
      // rate: '5'
    },

  ])

  const addCard = (addTitle, addImage, addDescription, addRate) => {
    const newCard = <Card
      title={addTitle}
      description={addDescription}
      // image: addImage,
      rate={addRate} />

    setCards(prevCards => {
      return [...prevCards, newCard]

    })
  }





  return (
    <div className="App">
      <h1>Movies List</h1>

      <CardsList mesCards={cards} handleAddCards={addCard} />

    </div>
  );
}

export default App;
