import React from 'react'
import Card from './Card'
import AAddCard from './AAddCard'
import './card.css'


export default function CardsList({ mesCards, handleAddCards }) {

    const [cards, setCards] = React.useState([
        {
            movieTitle: 'inception',
            movieDescription: 'new movie 2013 ....',
            // image: 'inception.jpeg',
            // rate: '5'
        },

    ])

    const addCard = (addTitle, addImage, addDescription, addRate) => {
        const newCard = <Card
            title={addTitle}
            description={addDescription}
            // image: addImage,
            rate={addRate} />
    }


    return (

        <div>
            <div className='movie-list'>
                {mesCards.map(card => (
                    <Card
                        title={card.movieTitle}
                        description={card.movieDescription}
                    // image={card.cardImage}
                    // rate={card.cardRrate}
                    />
                ))}
            </div>
            <AAddCard handleAddCards={addCard} />
        </div>
    )
}
