import Home from './Home';
import { Navbar, Container, Nav, NavLink } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'
import { Routes, Route, Link } from "react-router-dom";


function App() {











  // console.log(cards)
