  return (
    <div className="App">
      <header>
        <h1 className='app-title'>Movies List</h1>


      </header>
      <Navbar bg="dark" data-bs-theme="dark">
        <Container>
          <Navbar.Brand href="#home" >Home</Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link > <Link to="/movies">Movies</Link></Nav.Link>
            <Nav.Link><Link to="/">Home</Link></Nav.Link>
            <Nav.Link><Link to="/anime">Anime</Link></Nav.Link>


          </Nav>
        </Container>
      </Navbar>



      <Routes>
        <Route path='/' element={<Home />} />
        <Route path='/movies' element={<CardsList />} />
        <Route path='movies/:id' element={<Trailer />}></Route>
      </Routes>


    </div >
  );
}

export default App;





