import './App.css';

import React from 'react';
import CardsList from './CardsList';
import AAddCard from './AAddCard';

function App() {

  const [cards, setCards] = React.useState([
    {
      movieTitlea: 'inception',
      // image: 'inception.jpeg',
      movieDescriptiona: 'new movie 2013 ....',
      // rate: '5'
    },

  ])

  const addCard = (addTitle, addImage, addDescription, addRate) => {
    const newCard = {
      movieTitlea: addTitle,
      // image: addImage,
      cardDescription: addDescription,
      // rate: addRate,
    }

    setCards(prevCards => {
      return [...prevCards, newCard]

    })

  }

  return (
    <div className="App">
      <h1>Movies List</h1>

      <CardsList mesCards={cards} handleAddCards={addCard} />

    </div>
  );
}

export default App;
