import React, { useEffect } from 'react'
import { Link } from "react-router-dom";
import { Route, Routes, useNavigate, useParams, useSearchParams, useLocation } from 'react-router-dom';
import './card.css'

export default function Card({ id, title, image, rate, description, trailer }) {
    // const navigate = useNavigate()
    // const navigate = useNavigate()
    const dataToPass = { tr: trailer, des: description };
    // useEffect(() => {
    //     setTimeout(() => {
    //         // navigate('/movies/trailer/id', { state: trailer })
    //         navigate('/movies/trailer/id', { state: trailer })
    //     },)
    // }, [])

    return (

        <Link to={{ pathname: '/movies/trailer/', state: dataToPass }}  >
            <div className="card" >
                <img src={require(`./images/${image}`)} alt='poster' />
                <div className="content">
                    <h1 className="name">{title} </h1>
                    <h3 className="infos">
                        <i className="fa-solid fa-star"></i> {rate}/10
                    </h3>
                    <p className="short-desc">{description}</p>
                    <button className='trailer-button'>Watch Trailer <span style={{ display: 'none' }}>{trailer}</span></button>
                </div>
            </div>
        </Link>

    )
}
