import './App.css';

import React from 'react';
import CardsList from './CardsList';
import Card from './Card';

function App() {

  const [cards, setCards] = React.useState([
    {
      movieTitle: 'inception',
      movieDescription: 'new movie 2013 ....',
      // image: 'inception.jpeg',
      rate: '5'
    },

  ])
  const addCard = (addTitle, addDescription, addRate) => {

    setCards(prevCards => {
      return [...prevCards,
      <Card
        title={addTitle}
        description={addDescription}
        rate={addRate}
      // image: addImage,
      />]

    })
  }

  return (
    <div className="App">
      <h1>Movies List</h1>

      {/* <CardsList  handleAddCards={addCard} /> */}
      <CardsList mesCards={cards} handleSaveClick={addCard} />


    </div>
  );
}

export default App;
