import './App.css';
import MovieList from './components/MovieList';
import Card from './components/Card';
import React from 'react';
import CardsList from './components/CardsList';

function App() {

  const [cards, setCards] = React.useState([
    {
      title: 'geg',
      text: 'fdgdhd',
      image: 'inception.jpeg'
    },
    {
      title: 'geg',
      text: 'fdgdhd',
      image: 'inception.jpeg'
    },
    {
      title: 'geg',
      text: 'fdgdhd',
      image: 'inception.jpeg'
    }
  ])
  const addCard = (text) => {
    console.log(text)
    const newCard = {
      text: text,
    }
    const newCards = [...cards, newCard]
    setCards(newCards)
  }

  return (
    <div className="App">
      <h1>hello</h1>

      <CardsList mesCards={cards} handleAddCards={addCard} />

    </div>
  );
}

export default App;
