import React from 'react'
import Card from './Card'
import AAddCard from './AAddCard'
import './card.css'


export default function CardsList(props) {


    const finalList = props.mesCards.map(card => (
        <Card
            title={card.movieTitle}
            description={card.movieDescription}
            rate={card.movieRate}
            image={card.movieImage}
        />
    ))
    const [records, setRecords] = React.useState(cards)
    const Filter = (event) => {
        setRecords(cards.filter(f => f.movieTitle.toLowerCase().includes(event.target.value)))
    }


    return (

        <div>
            <div className='movie-list'>
                <input type='text' onChange={Filter} placeholder='Search' />
                {finalList}
            </div>
            <AAddCard handleAddCard={props.handleAdd} />
        </div>
    )
}
