import 'bootstrap/dist/css/bootstrap.min.css'
import { Container } from 'react-bootstrap'
import { useParams, useOutletContext, useLocation } from 'react-router-dom';
import Card from './Card'

export default function Trailer() {
    const trailer = useParams().id

    console.log(trailer)
    // useEffect(() => {
    //     console.log("params", params)

    // }, [])
    const obj = useOutletContext()
    console.log(obj)

    const location = useLocation()

    console.log(location)

    return (
        <div style={{ margin: '4rem 2rem' }} >
            <h1 style={{ color: 'white' }}> Trailer</h1>
            <p>
                {/* this page is for {trailer} */}
            </p>
            <Container>
                <div className="ratio ratio-21x9">
                    <iframe src={`https://www.youtube.com/embed/${trailer}`} title="YouTube video" allowFullScreen></iframe>
                </div>
                <div className='trailer-description'>
                </div>
            </Container >
            <p>
                {location.state}
            </p>



        </div >
    )
}
