import './App.css';

import React from 'react';
import CardsList from './CardsList';

function App() {

  const [cards, setCards] = React.useState([
    {
      title: 'inception',
      // image: 'inception.jpeg',
      // description: 'new movie 2013 ....',
      // rate: '5'
    },

  ])
  const addCard = (title, image, description, rate) => {
    const newCard = {
      title: title,
      // image: image,
      description: description,
      // rate: rate,
    }
    const newCards = [...cards, newCard]
    setCards(newCards)
  }

  return (
    <div className="App">
      <h1>Movies List</h1>

      <CardsList mesCards={cards} handleAddCards={addCard} />

    </div>
  );
}

export default App;
