import React, { useEffect, useState } from 'react'
import Card from './Card'
import AddCard from './AddCard'
import { nanoid } from 'nanoid'
import { Link, Outlet } from 'react-router-dom';

import './card.css'


export default function CardsList(props) {

    const [cards, setCards] = React.useState([
        {
            movieId: nanoid(),
            movieTitle: 'Interstellar',
            movieDescription: 'When Earth becomes uninhabitable in the future, a farmer and ex-NASA pilot, Joseph Cooper, is tasked to pilot a spacecraft, along with a team of researchers, to find a new planet for humans.',
            movieRate: '9',
            movieImage: 'interstellar.jpeg',
            movieTrailer: 'https://www.youtube.com/embed/zSWdZVtXT7E?si=lCROTziC5OkXkTT4',
        },
        {
            movieId: nanoid(),
            movieTitle: 'Inception',
            movieDescription: "Cobb steals information from his targets by entering their dreams. He is wanted for his alleged role in his wife's murder and his only chance at redemption is to perform a nearly impossible task.",
            movieRate: '7',
            movieImage: 'inception.jpeg',
            movieTrailer: 'https://www.youtube.com/embed/YoHD9XEInc0?si=6OpmNXNnyb0U7InV',
        },
        {
            movieId: nanoid(),
            movieTitle: 'Pride & prejudice',
            movieDescription: 'Elizabeth Bennett is a strong-willed yet sensible young woman. At a local ball, she encounters a wealthy young man, Mr Fitzwilliam Darcy, who is arrogant, and they develop an unusual relationship.',
            movieRate: '8',
            movieImage: 'pride&prejudice.jpeg',
            movieTrailer: 'https://www.youtube.com/embed/Ur_DIHs92NM?si=D2rFoEcPI1jrWWDq',
        },


    ])
    //  ---------------------------------------------------  //

    // --------------------- BlankCard  ------------------------- //
    const [show, setShow] = useState(false)
    function BlankCard(e) {
        if (e.target.id === 'blankCard') {
            setShow(true)
        }
        if (e.target.id === 'close') {
            setShow(false)
        }
    }
    //  ---------------------------------------------------  //

    // ----------------------  cardsList --------------------//

    //  ---------------------------------------------------  //
    // // ---------------------  Filter ------------------------- //

    const [filtered, setFiltered] = React.useState(cards)
    //  ---------------------------------------------------  //

    //  ------------------ add new Movie ------------------  //
    const newCard = {
        movieId: nanoid(),
        movieTitle: '',
        movieDescription: '',
        movieRate: '',
        movieImage: '',
        movieTrailer: ' ',
    }

    const add = function (titleAdded, descriptionAdded, rateAdded, imageAdded, trailerAdded) {
        setCards(prevCards => {
            return [{ ...prevCards, ...newCard, movieTitle: titleAdded, movieDescription: descriptionAdded, movieRate: rateAdded, movieImage: imageAdded = imageAdded ? imageAdded : 'open.jpg', movieTrailer: trailerAdded }]
        })
        setShow(false)
    }
    console.log(filtered)
    const cardsList = cards.map(card => (

        < Card
            id={card.movieId}
            title={card.movieTitle}
            description={card.movieDescription}
            rate={card.movieRate}
            image={card.movieImage}
            trailer={card.movieTrailer}
        />

    ))

    const Filter = (event) => {
        if (event.target.name === 'Title') {
            setFiltered(cardsList.filter(f => f.movieTitle.toLowerCase().includes(event.target.value)))
        } else if (event.target.name === 'Rate') {
            setFiltered(cardsList.filter(f => f.movieRate.includes(event.target.value)))
        }
        if (event.target.value === 'all') {
            setFiltered(cardsList)
        }
    }
    console.log(cardsList)
    //  --------------------------------------------------- //


    return (

        <>
            {/* Filter */}
            <div className="filter-inputs">
                <input type='text' onChange={Filter} placeholder='Search by title' name='Title' className='title-filter' />

                {/* Rate */}
                <select
                    value={newCard.newRate}
                    name='Rate'
                    className='rate-filter'
                    onChange={Filter}
                >
                    <option value={'all'}> Search by Rate</option>
                    <option value={'all'}> All </option>
                    <option value={1}> 1 </option>
                    <option value={2}> 2 </option>
                    <option value={3}> 3 </option>
                    <option value={4}> 4 </option>
                    <option value={5}> 5 </option>
                    <option value={6}> 6 </option>
                    <option value={7}> 7 </option>
                    <option value={8}> 8 </option>
                    <option value={9}> 9 </option>
                    <option value={10}> 10 </option>
                </select>
            </div>


            {/* --------------------- */}

            {show ?
                <>
                    <AddCard handleSaveNewCard={add} handleBlankCard={BlankCard} />
                    <div className='container' style={{ filter: 'blur(3px)' }}>
                        {[...cardsList,
                        <div className='blankCard  card '>
                            <img src={require('./images/plus.png')} id='blankCard' onClick={add} alt='plus' />
                        </div>]}
                    </div >
                </>
                :
                <div className='container' style={props.blurStyle}>
                    {[...cardsList,
                    <div className='blankCard  card '>
                        <img src={require('./images/plus.png')} id='blankCard' onClick={BlankCard} alt='plus' />
                    </div>]}
                </div>
            }





        </>
    )
}
