import React from 'react';
import CardsList from './CardsList';
import Card from './Card';

function App() {

  const [cards, setCards] = React.useState([
    {
      movieTitle: 'inception',
      movieDescription: 'new movie 2013 ....',
      // image: 'inception.jpeg',
      movieRate: '5'
    },

  ])

  const addTitle = (titleAdded) => {
    const newCard = {
      movieTitle: '',
      movieDescription: '',
      movieRate: ''
    }
    console.log(titleAdded)
    const newList = [...cards, { title: titleAdded, ...newCard }]
    setCards(newList)
  }
  const addDesc = (descriptionAdded) => {
    console.log(descriptionAdded)

  }

  return (
    <div className="App">
      <h1>Movies List</h1>

      {/* <CardsList  handleAddCards={addCard} /> */}
      <CardsList mesCards={cards} handleAddT={addTitle} handleAddD={addDesc} />


    </div>
  );
}

export default App;
