import React, { useState, useRef } from 'react';
import { Button, Card } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'
import './card.css'

export default function Cards() {
    const [cardData, setCardData] = useState(
        { image: '', title: '', description: '', rate: '', }
    )
    const inputRef = useRef(null)

    const handleImageClick = () => {
        inputRef.current.click()
    }
    const handleImageChange = (event) => {
        const file = event.target.files[0];
        console.log(file);
        setCardData(event.target.files[0])
        setformData(preFormData => {
            return {
                ...preFormData,
                [event.target.name]: event.target.files[0]
            }
        })
    }

    return (
        <div className='movie-card'>
            <div onClick={handleImageClick}>
                <label htmlFor='image-input'>
                    {
                        cardData.image ? <img className='upload-image' src={URL.createObjectURL(cardData.image)} alt='' /> : <img className='upload-image' src={require('../images/upload.jpeg')} alt='' />
                    }
                </label>

                <input type='file'
                    ref={inputRef}
                    name='image'
                    onChange={handleImageChange}
                    style={{ display: 'none' }} />
            </div>
            <input type='text'
                value=''
                name='title' />

        </div>




        //     <Card style={{ width: '18rem' }}>
