import React from 'react'
import Card from './Card'
import './card.css'
import AddCard from './AddCard'


export default function CardsList(props) {


    // ---------------------  Plus Card  ------------------------- //
    const [PlusCLick, setPlusCLick] = React.useState(false)

    function BlankCard(e) {
        if (e.target.id === 'blankCard') {
            setPlusCLick(true)
        }
    }
    //  ---------------------------------------------------  //

    const finalList = props.mesCards.map(card => (
        <Card
            title={card.movieTitle}
            description={card.movieDescription}
            rate={card.movieRate}
            image={card.movieImage}
        />
    ))


    return (
        <>
            <div className='container'>
                <div className='movie-cards'>
                    {finalList}
                    {/* {[...finalList,
                    <div className='blankCard' onClick={props.handleAddingCard} >
                        <img src={require('./images/plus.png')} alt='plus' id='blankCard' />
                    </div>]} */}
                </div>
            </div >
            <AddCard handleAddCard={props.handleAdd} />
        </>
    )
}
